jQuery(document).ready(function(){var a=jQuery("#contact-form").data("locale");jQuery.ajax({url:"/"+a+"/ajax/contact/getform/",type:"POST",data:{locale:a},dataType:"html",success:function(b){jQuery("#contact-form").html(b)}});jQuery(document).on("submit","#contact-form form",function(f){f.preventDefault();jQuery("#contact-form [type=submit]").attr("disabled","disabled");var b=jQuery("#contact-phone").val();var c=jQuery("#contact-email").val();var d=jQuery("#contact-message").val();jQuery.ajax({url:"/"+a+"/ajax/contact/post/",type:"POST",data:{locale:a,phone:b,email:c,message:d,},dataType:"json",success:function(e){if(e.result=="valid"){jQuery("#contact-form").html(e.html)}else{jQuery("#contact-form").prepend(e.html);jQuery("#contact-form [type=submit]").removeAttr("disabled")}}})})});
